Activity.create(name: "Hike the Middlesex Fells Reservation", blurb: "There are lots of great hiking trails to choose from, with really great views at the top. Bring a picnic and make a day of it!", url: "http://mass-parks.com", things_needed: "Water bottle, comfortable shoes", user_id: 1, map: activity_1_map)

Activity.create(name: "Have a drink at the bar at the Liberty Hotel", blurb: "This hotel used to be the Charles Street Jail, and has since been converted into one of the swankiest hotels in Boston. Have a drink at the sleek bar in the lobby, and while you’re there, check out the cool artwork on the walls.", url: "http://www.libertyhotel.com/food_and_drink/liberty_bar.html", things_needed: "Desire to see and be seen", user_id: 3, map: activity_4_map)

Activity.create(name: "Go for a run by the Charles at sunset", blurb: "See some of the best views of the city as you run along the river on this 7 mile loop. The route starts near the Longfellow Bridge on the Boston side, and takes you all the way out the Harvard Business School and back around.", url: "http://www.bostonmagazine.com/health/blog/2012/09/20/best-running-routes-boston/ ", things_needed: "Running shoes, decent cardiovascular endurance", user_id: 1, map: activity_5_map)

Activity.create(name: "Visit the Mapparium at the Mary Baker Eddy library", blurb: "A 30-foot glass bridge takes you into the middle of a giant glass globe, giving you an inside-out view of the world as it was in 1935.", url: "http://www.marybakereddylibrary.org/exhibits/mapparium", things_needed: "Fascination with giant globes", user_id: 2, map: activity_6_map)

Activity.create(name: "Do your own cannoli tasting in the North End", blurb: "Decide for yourself who has the best cannolis in the North End - make stops at Mike’s Pastry, Maria’s Pastry Shop, Bova’s Bakery, and Modern Pastry.", url: "http://boston.cbslocal.com/top-lists/best-bakeries-in-bostons-north-end/", things_needed: "A sweet tooth and an empty stomach", user_id: 3, map: activity_7_map)

Activity.create(name: "Take a tour of Harpoon Brewery", blurb: "Harpoon Brewery offers weekend tours of their facilities, and tastings every weekday. A tour only costs $5! ", url: "http://www.harpoonbrewery.com/", things_needed: "A love of local beer", user_id: 3, map: activity_8_map)
